//= require react
//= require react_ujs
//= require_tree ./services
//= require_tree ./components

$(document).ready(function(){
  // FIXME: react_ujs is suppose to do this for us. But it's not working.
  // This is a handler to convert the components generated by `react_component`
  $("[data-react-class]").each(function(i, elm) { 
    var $elm = $(elm);
    ReactDOM.render(React.createElement(eval($elm.data('react-class')), eval($elm.data('react-props'))), elm);
  });
});
